        return (
            <div className="mainPage">

                <div className="customer">
                    <Button>customer!</Button>
                </div>
                <div className="host">
                    <Button onClick={this.displayHostForm}>host!</Button>
                </div>
            </div>
        )
    }
}

export default MainPage;
